import { Router } from "express";
import { methods as languageControler } from "./../controllers/language.controller";
const router = Router();

router.get("/", languageControler.getLanguage);

router.post("/", languageControler.regisLanguage);

router.get("/:id", languageControler.getLanguagebyID);

router.delete("/:id", languageControler.deletLenguage);

router.put("/:id", languageControler.updateLanguage)


export default router

