import { getConnection } from "./../database/database";

//consultar a la base de datos
const getLanguage = async (req, res) => {
    try{
        const connection = await getConnection();
        const result = await connection.query("SELECT id, name, programmers FROM language");
        res.json(result);
    }catch(error){
        res.status(500);
        res.send(error.message);
    }
   
};

//registrar lenguaje
const regisLanguage = async (req, res) => {
    try{
        const { name , programmers }= req.body;

        if(name == undefined || programmers == undefined) {
            res.status(400).json({message: "Bad request. Please fill all field"});
        }

        const language = {name, programmers} 
        const connection = await getConnection();
        const result = await connection.query("INSERT INTO language SET ?", language)
          
        res.json({message: "Language added "});

        //res.json(result);
    }catch(error){
        res.status(500);
        res.send(error.message);
    }
};

//Obtener lenguaje por ID
const getLanguagebyID = async (req, res) => {
    try{
        
        const { id } = req.params;
        const connection = await getConnection();
        const result = await connection.query("SELECT id, name, programmers FROM language WHERE id = ?", id);
        res.json(result);
    }catch(error){
        res.status(500);
        res.send(error.message);
    }
};

//eliminar un lenguaje
const deletLenguage = async (req, res) => {
    try{
        const { id } = req.params;
        const connection = await getConnection();
        const result = await connection.query("DELETE FROM language WHERE id = ?", id);
        res.json(result);
    }catch(error){
        res.status(500);
        res.send(error.message);
    }
   
};

//actualizar elemento
const updateLanguage = async (req, res) => {
    try{

        const { id } = req.params;
        const { name, programmers } = req.body;

        if (id == undefined || name == undefined || programmers == undefined) {
            res.status(400).json({message: "Bad request. Please fill all field"});
        }

        const language = { name, programmers };
        const connection = await getConnection();
        const result = await connection.query("UPDATE Language SET ? WHERE id = ?", [language, id]);
        res.json(result);
    }catch(error){
        res.status(500);
        res.send(error.message); 
    }
   
};

export const methods = {
    getLanguage,
    regisLanguage,
    getLanguagebyID,
    deletLenguage,
    updateLanguage
}

