sourcesContent:
import { getConnection } from "../database/database";

const getBoards = async (req, res) => {
    try {
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM pizarras");
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

const getBoard = async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM pizarras WHERE id = ?", [id]);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

const addBoard = async (req, res) => {
    try {
        const { nombre, id_docente } = req.body;
        const connection = await getConnection();
        const result = await connection.query("INSERT INTO pizarras (nombre, id_docente) VALUES (?, ?)", [nombre, id_docente]);
        res.json({ message: "Pizarra aadida", id: result.insertId });
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

const deleteBoard = async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
        const result = await connection.query("DELETE FROM pizarras WHERE id = ?", [id]);
        res.json({ message: "Pizarra eliminada" });
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

export const methods = {
    getBoards,
    getBoard,
    addBoard,
    deleteBoard
};

