import { Router } from "express";
import { methods as boardController } from "../controllers/board.controller";
import verifyToken from "../middleware/auth";

const router = Router();

router.get("/", boardController.getBoards);
router.get("/:id", boardController.getBoard); // Ruta para obtener una pizarra especfica
router.post("/", boardController.addBoard);
router.delete("/:id", boardController.deleteBoard);
router.get("/docente/:id_docente", boardController.getBoardsByDocente); // Nueva ruta para obtener pizarras por docente
router.post("/save", verifyToken, boardController.saveBoardContent); // Ruta para guardar el contenido de la pizarra
router.get("/content/:id", boardController.getBoardContent); // Ruta para obtener el contenido de la pizarra

export default router;

